import React, { Component } from 'react';
import apiModel from '../models/apiModel';
import Update from '../components/Update'

class UpdateContainer extends Component {
    state = {
        customer: []
    }
    componentDidMount() {
        this.fetchData();
    };
    fetchData = () => {
        apiModel.all()
            .then((data) => {
                this.setState({ customer: data });
            });
    };
    updateCustomer = (customer) => {
        const isUpdatedCustomer = c => {
            return c._id === customer._id;
        }
        apiModel.update(customer)
            .then(data => {
                let customers = this.state.customers;
                customers.find(isUpdatedCustomer).body = customer.body;
                this.setState({ customers });
            })
    }
    render() {
        let indexList
        if (this.state.customers) {
            indexList = this.state.customers.map((customer, index) => {
                return <AllCustomers customer={customer} key={index} />
            })
        }
        return (
            <div className='contain'>
                <Update 
                    updateCustomer={this.updateCustomer}
                />
                <div id='customer'>
                    <p className='names'>First Name</p>
                    <p className='names'>Last Name</p>
                    <p className='home'>Home City</p>
                </div>
                {indexList}
            </div>

        )
    }
}

export default UpdateContainer;
