import React, { Component } from 'react';
import AllCustomers from './components/AllCustomers';

class Update extends Component {
    state = {
        first_name: '',
        last_name: '',
        home_city: '',
    }
    onChange = (event) => {
        this.setState({
            //name is input for first_name, last_name, home_city
            [event.target.name]: event.target.value
        });
    };
    onSubmit = (event) => {
        event.preventDefault();
        let customer = this.state;
        this.props.createCustomer(customer);
    };
    render() {
        let indexList
        if (this.state.customers) {
            indexList = this.state.customers.map((customer, index) => {
                return <AllCustomers customer={customer} key={index} />
            })
        }
        return(
            <div>
                <p>hi</p>
                <p className='name'> {this.props.customer.first_name}</p>
                <p className='name'> {this.props.customer.last_name}</p>
                <p className='homecity'> {this.props.customer.home_city}</p>
            </div>
        )
    }
}

export default Update;
