import React, { Component } from 'react';
import apiModel from '../models/apiModel';
import Create from '../components/Create';

class CreateContainer extends Component {
    state = {
        customer: []
    }
    componentDidMount() {
        this.fetchData();
    };

    fetchData = () => {
        apiModel.all()
            .then((data) => {
                this.setState({ customers: data });
            });
    };

    createCustomer = (customer) => {
        // let newCustomer = {
