            // customer.push(res);
            // this.setState({ customer: customer })
        // });
    };
    render() {
        return (
            <div className='contain'>
                <h2>Add a customer information!</h2>
                <Create
                    createCustomer={this.createCustomer}
                />
            </div>

        )
    }
}

export default CreateContainer;
