import React, { Component } from 'react';

class Create extends Component {
    state = {
        customer: '',
    }
    onInputChange = (event) => {
        this.setState({
            customer: event.target.value
        });
    };
    onFormSubmit = (event) => {
        event.preventDefault();
        let customer = this.state.customer;
        this.props.createCustomer(customer);
        this.setState({ customer: '' });
    };

    render() {
        return(
            <form onSubmit = {this.props.handleSubmit}>
                <input type='text' name='firstName' onChange={this.props.handleChange} value={this.props.content} />
                <input type='text' name='lastName' onChange={this.props.handleChange} value={this.props.content} />
                <input type='text' name='homeCity' onChange={this.props.handleChange} value={this.props.content} />
                <button type='submit'>Save</button>
            </form>
        )
    }
}

export default Create;
