import React, { Component } from 'react';

class OneCustomer extends Component {
    state = {
        rowid: null
    }
    onChange = (event) => {
        this.setState({
            //name is input for first_name, last_name, home_city
            [event.target.name]: event.target.value
        });
    };
    onSubmit = (event) => {
        event.preventDefault();
        let customer = this.state;
        this.props.updateCustomer(customer);
    };

    render() {
        return(
            <div id='customers'>
                <p>{this.props.customer.rowid}</p>
                <p className='name'> {this.props.customer.first_name}</p>
                <p className='name'> {this.props.customer.last_name}</p>
                <p className='homecity'> {this.props.customer.home_city}</p>
            </div>
        )
    }
}

export default OneCustomer;
